const style = {

    container:
      {
      bgcolor: "#212529",
      width: "100vw",
      height: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
      },

      login: {
        fontSize: 14.44,
        fontWeight: "bolder",
        color: "white",
      },

      form:{
        width: "500px",
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        gap: "15px"
      },

      box:{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        gap: "15px"
      },

      input:{
        bgcolor: "#343B41",
        '&:hover': {
          border: "1px white solid",
        }
      },

      buttonLogin:{
        bgcolor: "#FF577F",
        width: "10,4px",
        height: "10,15px",
        '&:hover': {
          bgcolor: "#FF427F",
        }
      }


}

export default style
