import { useForm } from "react-hook-form";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";

function ModalEdit({ setModalEdit }) {
    const token = localStorage.getItem("KenzieHub:Token");

    const onSubmit = (dataForm) => {
        ApiKenzieHub.put(
            `/users/techs/${dataForm.id}`,
            { title: dataForm.title, status: dataForm.status },
            { headers: { Authorization: `Bearer ${token}` } }
        )
            .then((res) => {
                setModalEdit(false);
                toast.success("Tecnologia atualiza");
            })
            .catch((error) => toast.error("Ops algo deu errado"));

        const removeTechnology = (id) => {
            ApiKenzieHub.delete(`/users/techs/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
                .then((res) => {
                    toast.warning("Tecnologia Deletada");
                })
                .catch((error) => toast.error("Ops algo deu errado"));
        };
    };
    return (
        <section>
            <div>
                <h3>Tecnologias Detalhes</h3>
                <button onClick={() => setModalEdit(false)}>X</button>
            </div>
