import style from "./styles.js";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";
// import Logo from "../../Images/Logo.svg";

function FormLogin() {
    const formSchema = yup.object().shape({
        email: yup.string().required("Digite seu email"),
        password: yup.string().required("Digite sua senha"),
    });

    const {
        reset,
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
    });

    const onSubmitFunction = async (dataForm) => {
        await ApiKenzieHub.post("/sessions/", dataForm)
            .then((res) => {
                toast.success("Authentication performed successfully!");
                reset();
            })
            .catch((error) => {
                toast.error("Authentication Failed, incorrect login or password!");
            });
    };

    return (
        <section sx={style.container}>
            {/* <Logo /> */}
            <form onSubmit={handleSubmit(onSubmitFunction)}>
                <p sx={style.login}>Login</p>

                <p sx={{ color: "white" }}>E-mail</p>
                <div sx={style.div}>
                    <input
                        sx={style.input}
                        error={errors.email?.message}
                        helperText={errors.email?.message}
                        {...register("email")}
                        variant="outlined"
                    />

                    <p sx={{ color: "white" }}>Senha</p>
                    <input
                        sx={style.input}
                        error={errors.password?.message}
                        helperText={errors.password?.message}
                        type="password"
                        {...register("password")}
                        variant="outlined"
                    />
                </div>
                <div>
                    <button type="submit" variant="contained">
                        Entrar
                    </button>
                </div>

                <p>Ainda não possui uma conta?</p>

                <button type="submit" variant="contained">
                    Cadastre-se
                </button>
            </form>
        </section>
    );
}

export default FormLogin;
