import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Logo from "../../Images/Logo.svg";

function FormRegister() {
    const history = useHistory();
    const [select, setSelect] = useState("");

    const formSchema = yup.object().shape({
        name: yup.string().required("Digite seu nome"),
        email: yup.string().required("Digite seu email"),
        bio: yup.string().required("Nos conte algo sobre você"),
        contact: yup.string().required("Nos dê uma forma de contato"),
        password: yup.string().required("Digite sua senha"),
        passwordConfirmed: yup
            .string()
            .oneOf([yup.ref("password")], "Senhas diferentes")
            .required("Confirme sua senha"),
        course_module: yup.string().required("Selecione seu módulo"),
    });

    const {
        reset,
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
    });

    const onSubmitFunction = async (dataForm) => {
        delete dataForm.passwordConfirmed;
        console.log(dataForm);
        await ApiKenzieHub.post("/users", dataForm)
            .then((res) => {
                toast.success("Conta criada com sucesso");
                reset();
                history.push("/");
            })
            .catch((error) => {
                toast.error("Ops! Algo deu errado");
            });
    };

    return (
        <section>
            <form onSubmit={handleSubmit(onSubmitFunction)}>
                <h4>Crie sua conta</h4>
                <p>Rápido egrátis, vamos nessa</p>

                <div>
                    <p>Nome</p>
                    <input error={errors.name?.message} {...register("name")} />
                    <p>{errors.name && errors.name.message}</p>

                    <p>E-mail</p>
                    <input error={errors.email?.message} {...register("email")} />
                    <p>{errors.email && errors.email.message}</p>

                    <p>Biografia</p>
                    <input error={errors.bio?.message} {...register("bio")} />
                    <p>{errors.bio && errors.bio.message}</p>

                    <p>Contato</p>
                    <input error={errors.contact?.message} {...register("contact")} />
                    <p>{errors.contact && errors.contact.message}</p>

                    <p>Senha</p>
                    <input
                        error={errors.password?.message}
                        type="password"
                        {...register("password")}
                    />
                    <p>{errors.password && errors.password.message}</p>

                    <p>Confirmar Senha</p>
                    <input
                        error={errors.passwordConfirmed?.message}
                        type="password"
                        {...register("passwordConfirmed")}
                    />
                    <p>{errors.passwordConfirmed && errors.passwordConfirmed.message}</p>

                    <p>Selecionar Módulo</p>
                    <select
                        className="Select"
                        defaultValue={select}
                        onChange={(e) => setSelect(e.target.value)}
                        {...register("course_module")}
                    >
                        <option value="Primeiro Módulo" selected>
                            Primeiro Módulo
                        </option>
                        <option value="Segundo Módulo">Segundo Módulo</option>
                        <option value="Terceiro Módulo" selected>
                            Terceiro Módulo
                        </option>
                        <option value="Quarto Módulo">Quarto Módulo</option>
                    </select>
                </div>

                <div>
                    <button>Cadastrar</button>
                </div>
            </form>
        </section>
    );
}

export default FormRegister;
