import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import React, { useEffect, useState } from "react";
import ModalAdd from "../../Components/ModalAdd";
import ModalEdit from "../../Components/ModalEdit";
import ApiKenzieHub from "../../Services";

function Home({ authenticate }) {
    const [modalAdd, setModalAdd] = useState(false);
    const [modalEdit, setModalEdit] = useState(false);
    const [technology, setTechnology] = useState([]);
    const [techs, setTechs] = useState({});

    const history = useHistory();

    useEffect(() => {
        const userId = localStorage.getItem("User:id");
        ApiKenzieHub.get(`/users/${userId}`).then((res) => {
            setTechnology(res.data.techs);
        });
    }, []);

    const openModalEdit = (id) => {
        console.log(technology.find((item) => item.id === id));
        setTechs(technology.find((item) => item.id === id));
        setModalEdit(true);
    };

    if (!authenticate) {
        toast.success("Você saiu da sua conta");
        history.push("/");
    }

    return (
        <>
            <button
                onClick={() => {
                    document.location.reload(true);
                    localStorage.clear();
                }}
            >
                Sair
            </button>

            {technology.map((item) => (
                <div onClick={openModalEdit}>
                    <span>{item.title}</span>
                    <span>{item.status}</span>
                </div>
            ))}

            {!modalAdd ? <></> : <ModalAdd setModalAdd={setModalAdd} />}
            {!modalEdit ? <></> : <ModalEdit setModalEdit={setModalEdit} technology={techs} />}
        </>
    );
}

export default Home;
