      bgcolor: "#212529",
      width: "100vw",
      height: "100vh",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
      },

      login: {
        fontSize: 14.44,
        fontWeight: "bolder",
        color: "white",
      },

      form:{
        width: "500px",
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        gap: "15px"
      },

      box:{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        gap: "15px"
      },

      //como mudar a cor da letra e como colocar focus????
      input:{

        bgcolor: "#343B41",
        borderRadius: "4px",
        '&:focused': {
          bgcolor: "white",
        }
      },

      buttonLogin:{
        bgcolor: "#FF577F",
        width: "227.4px",
        height: "50px",
        border: "1.2182px solid #FF577F",
        marginBottom: "20px",
        '&:hover': {
          bgcolor: "#FF427F",
        }
      },

      buttonGoRegister:{
        bgcolor: "#868E96",
        width: "227.4px",
        height: "50px",
        marginBottom: "20px",
        '&:hover': {
          bgcolor: "#343B41",
        }
      },


}

export default style
