import style from "./styles.css";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";
import { useHistory } from "react-router-dom";
import Logo from "../../Images/Logo.svg";

function FormLogin() {
    const history = useHistory();

    const formSchema = yup.object().shape({
        email: yup.string().required("Digite seu email"),
        password: yup.string().required("Digite sua senha"),
    });

    const {
        reset,
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
    });

    const goRegister = () => {
        history.push("/register");
    };

    const onSubmitFunction = async (dataForm) => {
        await ApiKenzieHub.post("/sessions/", dataForm)
            .then((res) => {
                toast.success("Login realizado com sucesso");
                reset();
                history.push("/home");
            })
            .catch((error) => {
                toast.error("Ops! Algo deu errado");
            });
    };

    return (
        <section>
            <div>
                <img src={Logo} alt="Logo Nu Kenzie" />
            </div>

            <form onSubmit={handleSubmit(onSubmitFunction)}>
                <p>Login</p>

                <p>E-mail</p>
                <div>
                    <input
                        sx={style.input}
                        error={errors.email?.message}
                        {...register("email")}
                        variant="outlined"
                    />
                    <p>{errors.email && errors.email.message}</p>

                    <p>Senha</p>
                    <input
                        sx={style.input}
                        error={errors.password?.message}
                        type="password"
                        {...register("password")}
                    />
                    <p>{errors.password && errors.password.message}</p>
                </div>

                <div>
                    <button type="submit" variant="contained">
                        Entrar
                    </button>
                </div>

                <p>Ainda n√£o possui uma conta?</p>

                <button onClick={goRegister}>Cadastre-se</button>
            </form>
        </section>
    );
}

export default FormLogin;
