import "./styles.css";
import { useForm } from "react-hook-form";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";

function ModalEdit({ setModalEdit }) {
    const token = localStorage.getItem("KenzieHub:Token");

    const { register, handleSubmit } = useForm();

    const onSubmit = (dataForm) => {
        ApiKenzieHub.put(
            `/users/techs/${dataForm.id}`,
            { title: dataForm.title, status: dataForm.status },
            { headers: { Authorization: `Bearer ${token}` } }
        ).then((res) => {
            setModalEdit(false);
            toast.success("Tecnologia atualiza");
        });

        const removeTechnology = (dataForm) => {
            ApiKenzieHub.delete(`/users/techs/${dataForm.id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            }).then((response) => {
                toast.warning("Tecnologia Deletada");
            });
        };
    };
    return (
        <section className="container_modal_edit">
            <div className="header_modal_edit">
                <h3>Tecnologias Detalhes</h3>
                <button className="close_modal_edit" onClick={() => setModalEdit(false)}>
                    X
                </button>
            </div>
            <div className="general_modal_edit">
                <form className="form_modal_edit" onSubmit={handleSubmit(onSubmit)}>
                    <p className="p_modal_edit">Nome do projeto</p>
                    <input className="input_modal_edit" {...register("title")} />

                    <p className="p_modal_edit">Status</p>
                    <select {...register("status")}>
                        <option value="Iniciante" selected>
                            Iniciante
                        </option>
                        <option value="Intermediário">Intermediário</option>
                        <option value="Avançado" selected>
                            Avançado
                        </option>
                    </select>
                    <div className="buttons_modal_edit">
                        <button className="edit_modal_edit"> Salvar alterações </button>
                        <button className="delete_modal_edit"> Excluir </button>
                    </div>
                </form>
            </div>
        </section>
    );
}

export default ModalEdit;
