import style from "./styles.js";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { Image, Box, Button, Container, FormControl, TextField, Typography } from "@mui/material";
import { toast } from "react-toastify";

function FormLogin() {
    const formSchema = yup.object().shape({
        email: yup.string().required("Digite seu email"),
        password: yup.string().required("Digite sua senha"),
    });

    const {
        reset,
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
    });

    const onSubmitFunction = async (dataForm) => {
        await ApiKenzieHub.post("/sessions/", dataForm)
            .then((res) => {
                toast.success("Authentication performed successfully!");
                reset();
            })
            .catch((error) => {
                toast.error("Authentication Failed, incorrect login or password!");
            });
    };

    return (
        <Container sx={style.container}>
            {/* <Image src="./Logo.svg" /> */}
            <FormControl sx={style.form} component="form" onSubmit={handleSubmit(onSubmitFunction)}>
                <Typography sx={style.login}>Login</Typography>

                <Typography>E-mail</Typography>
                <Box sx={style.box}>
                    <TextField
                        autoFocus={true}
                        sx={style.input}
                        error={errors.email?.message}
                        helperText={errors.email?.message}
                        {...register("email")}
                        variant="filled"
                    />

                    <Typography sx={{ textAlign: "left" }}>Senha</Typography>
                    <TextField
                        sx={style.input}
                        error={errors.password?.message}
                        helperText={errors.password?.message}
                        type="password"
                        {...register("password")}
                        variant="filled"
                    />
                </Box>
                <Box>
                    <Button sx={style.buttonLogin} type="submit" variant="contained">
                        Entrar
                    </Button>
                </Box>
                <Typography sx={{ color: "white" }} variant="p">
                    Ainda não possui uma conta?
                </Typography>
                <Button sx={style.buttonGoRegister} type="submit" variant="contained">
                    Cadastre-se
                </Button>
            </FormControl>
        </Container>
    );
}

export default FormLogin;
