import "./styles.css";
import { useForm } from "react-hook-form";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";

function ModalEdit({ setModalEdit }) {
    const token = localStorage.getItem("KenzieHub:Token");

    const { register, handleSubmit } = useForm();

    const onSubmit = (dataForm) => {
        ApiKenzieHub.put(
            `/users/techs/${dataForm.id}`,
            { title: dataForm.title, status: dataForm.status },
            { headers: { Authorization: `Bearer ${token}` } }
        ).then((res) => {
            setModalEdit(false);
            toast.success("Tecnologia atualiza");
        });

        const removeTechnology = (id) => {
            ApiKenzieHub.delete(`/users/techs/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            }).then((response) => {
                toast.warning("Tecnologia Deletada");
            });
        };
    };
    return (
        <section>
            <div>
                <h3>Tecnologias Detalhes</h3>
                <button onClick={() => setModalEdit(false)}>X</button>
            </div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <p>Nome do projeto</p>
                <input {...register("title")} />

                <p>Status</p>
                <select {...register("status")}>
                    <option value="Iniciante" selected>
                        Iniciante
                    </option>
                    <option value="Intermediário">Intermediário</option>
                    <option value="Avançado" selected>
                        Avançado
                    </option>
                </select>
                <button> Salvar alterações </button>
                <button> Excluir </button>
            </form>
        </section>
    );
}

export default ModalEdit;
