import { Redirect } from "react-router-dom";
import React, { useEffect, useState } from "react";
import ModalAdd from "../../Components/ModalAdd";
import ModalEdit from "../../Components/ModalEdit";
import ApiKenzieHub from "../../Services";
import { toast } from "react-hot-toast";

function Home({ authenticate, setAuthenticate }) {
    const [modalAdd, setModalAdd] = useState(false);
    const [modalEdit, setModalEdit] = useState(false);
    const [technology, setTechnology] = useState([]);
    const [techs, setTechs] = useState({});
    const [refresh, setRefresh] = useState(false);

    useEffect(() => {
        const userId = localStorage.getItem("User:id");
        ApiKenzieHub.get(`/users/${userId}`).then((res) => {
            setTechnology(res.data.techs);
        });
    }, [refresh]);

    function exit() {
        setAuthenticate(false);
        localStorage.clear();
        toast("Usuário deslogado");
    }

    const openModalEdit = (id) => {
        setTechs(technology.find((item) => item.id === id));
        setModalEdit(true);
        setRefresh(!refresh);
    };

    if (!authenticate) {
        return <Redirect to="/" />;
    }

    return (
        <>
            <button onClick={exit}>Sair</button>

            {technology.map((item) => (
                <div onClick={() => openModalEdit(item.id)}>
                    <span>{item.title}</span>
                    <span>{item.status}</span>
                </div>
            ))}

            {!modalAdd ? (
                <></>
            ) : (
                <ModalAdd setModalAdd={setModalAdd} refresh={refresh} setRefresh={setRefresh} />
            )}
            {!modalEdit ? (
                <></>
            ) : (
                <ModalEdit
                    setModalEdit={setModalEdit}
                    technology={techs}
                    refresh={refresh}
                    setRefresh={setRefresh}
                />
            )}
        </>
    );
}

export default Home;
