import { useHistory } from "react-router-dom";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React, { useState } from "react";

function Home({ authenticate }) {
    const [modalAdd, setModalAdd] = useState(true);

    const history = useHistory();

    if (authenticate === false) {
        toast.success("Você saiu da sua conta");
        history.push("/");
    }

    const token = localStorage.getItem("KenzieHub:Token");
    const schema = yup.object().shape({
        title: yup.string().required("Campo Obrigatório"),
    });
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(schema),
    });

    const onSubmit = (dataForm) => {
        if (dataForm === "") {
            return toast.error("Preencha todos os campos");
        }

        ApiKenzieHub.post(
            "/users/techs",
            {
                title: dataForm.title,
                status: dataForm.status,
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            }
        )
            .then((res) => {
                setModalAdd(false);
                toast.success("Tecnologia adicionada com sucesso");
            })
            .catch((error) => toast.error("Ops algo deu errado"));
    };

    return (
        <>
            {modalAdd === false ? (
                <>
                    <button
                        onClick={() => {
                            document.location.reload(true);
                            localStorage.clear();
                        }}
                    >
                        Sair
                    </button>
                </>
            ) : (
                <section>
                    <div>
                        <h3>Cadastrar Tecnologias</h3>
                        <button onClick={() => setModalAdd(false)}>X</button>
                    </div>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <p className="p_title">Tecnologia</p>
                        <input error={errors.title?.message} {...register("title")} />
                        <p>{errors.title && errors.title.message}</p>

                        <select className="select_modal_add" {...register("status")}>
                            <option value="Iniciante" selected>
                                Iniciante
                            </option>
                            <option value="Intermediário">Intermediário</option>
                            <option value="Avançado" selected>
                                Avançado
                            </option>
                        </select>
                        <button> Cadastrar </button>
                    </form>
                </section>
            )}
        </>
    );
}

export default Home;
