import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import ApiKenzieHub from "../../Services";
import { toast } from "react-toastify";
import React from "react";
import { useHistory } from "react-router-dom";
import Logo from "../../Images/Logo.svg";

function FormRegister() {
    const formSchema = yup.object().shape({
        name: yup.string().required("Digite seu nome"),
        email: yup.string().required("Digite seu email"),
        password: yup.string().required("Digite sua senha"),
        passwordConfirmed: yup
            .string()
            .oneOf([yup.ref("password")], "Senhas diferentes")
            .required("Confirme sua senha"),
    });

    const {
        reset,
        register,
        handleSubmit,
        formState: { errors },
    } = useForm({
        resolver: yupResolver(formSchema),
    });

    const onSubmitFunction = async (dataForm) => {
        await ApiKenzieHub.post("/users/", dataForm)
            .then((res) => {
                delete dataForm.passwordConfirmed;
                toast.success("Conta criada com sucesso");
                reset();
            })
            .catch((error) => {
                toast.error("Ops! Algo deu errado");
            });
    };

    return (
        <section>
            <form onSubmit={handleSubmit(onSubmitFunction)}>
                <h4>Crie sua conta</h4>
                <p>Rápido egrátis, vamos nessa</p>

                <div>
                    <p>Nome</p>
                    <input error={errors.name?.message} {...register("name")} />
                    <p>{errors.name && errors.name.message}</p>

                    <p>E-mail</p>
                    <input error={errors.email?.message} {...register("email")} />
                    <p>{errors.email && errors.email.message}</p>

                    <p>Senha</p>
                    <input
                        error={errors.password?.message}
                        type="password"
                        {...register("password")}
                    />
                    <p>{errors.password && errors.password.message}</p>

                    <p>Confirmar Senha</p>
                    <input
                        error={errors.passwordConfirmed?.message}
                        type="password"
                        {...register("passwordConfirmed")}
                    />
                    <p>{errors.passwordConfirmed && errors.passwordConfirmed.message}</p>
                </div>

                <div>
                    <button>Cadastrar</button>
                </div>
            </form>
        </section>
    );
}

export default FormRegister;
